-ifdef(PROPER).
proper_test_() ->
    {"Runs " ?MODULE_STRING " PropEr tests"
    ,[{atom_to_list(F)
      ,fun () ->
               ?assert(proper:quickcheck(?MODULE:F(), [{'to_file', 'user'}
                                                      ,{'numtests', 500}
                                                      ]))
       end
      }
      || {F, 0} <- ?MODULE:module_info('exports'),
         F > 'prop_',
         F < 'prop`'
     ]
    }.
